function loadDescription() {
    const description = localStorage.getItem('hotelDescription') || `<p>Bem-vindo ao UTFPR Hotéis, onde conforto, sofisticação e a conexão com a natureza se encontram. Localizado em
          um ambiente privilegiado, cercado por paisagens naturais deslumbrantes, nosso hotel é o refúgio perfeito para
          quem busca tranquilidade e bem-estar.</p>

        <p>Oferecemos acomodações modernas e aconchegantes, ideais para relaxar após um longo dia, além de áreas verdes
          que convidam ao descanso e à contemplação. Desfrute de uma experiência única com serviços de alta qualidade,
          equipe atenciosa e uma estrutura completa que inclui restaurante, piscina, academia, trilhas ecológicas e
          espaços para eventos.</p>

        <p>Seja a sua visita a negócios ou lazer, garantimos uma estadia inesquecível, onde a harmonia entre conforto e
          natureza faz toda a diferença. Faça sua reserva e venha viver momentos memoráveis conosco!</p>`;
    document.getElementById('descricao').innerHTML = description;
  }

  function loadCell(){
    const cell = localStorage.getItem('contactInfo') || `<p class="text-center m-0 fs-5" id="telefone">+55 (46) 3536-8900</p>
        <p class="text-center m-0 fs-5" id="email">cogerh-dv@utfpr.edu.br</p>`;
    document.getElementById("contato").innerHTML = cell;
    console.log(document.getElementById("contato"))
  }

  function loadPhotos() {
    const photos = JSON.parse(localStorage.getItem('hotelPhotos')) || ['https://dirgrad.dv.utfpr.edu.br/site/wp-content/uploads/2022/05/bloco_k-1920x692.jpg','https://www.utfpr.edu.br/campus/doisvizinhos/sobre/dois.JPG/@@images/image','https://rbj.com.br/wp-content/uploads/2022/06/image-1920x1280.jpeg'];
    if (photos.length != 0) {
      if (photos.length == 1) {
        const nxtBtn = document.getElementById("nextButton")
        const prvBtn = document.getElementById("previousButton")
        nxtBtn.style.display = 'none'
        prvBtn.style.display = 'none'
      }
    const gallery = document.getElementById('carrossel');
    gallery.innerHTML = photos.map((photo, index) => `
      <div class="carousel-item${index === 0 ? ' active' : ''}">
        <img src="${photo}" alt="Foto do Hotel" class="d-block w-100"></img>
      </div>
    `).join('');
    }else{
      document.getElementById("carrosselDiv").remove()
    }
  }

  document.addEventListener('DOMContentLoaded', () => {
    loadCell();
    loadDescription();
    loadPhotos();
  });